}
  render(){
  return (
    <div className="App">
    <br /><br /><br />
  <button className="btn btn-success" onClick={()=>{this.setState({form: null, tipoModal: 'insertar'}); this.modalInsertar()}}>Agregar pregunta</button>
  <br /><br />
    <table className="table ">
      <thead>
        <tr>
          <th>Tipo</th>
          <th>Enunciado</th>
          <th>Respuesta verdadera</th>
          <th>Respuesta falsa 1</th>
          <th>Respuesta falsa 2</th>
          <th>Respuesta falsa 3</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
      {this.state.data.map(pregunta=>{
          return(
            <tr>
            <td>{pregunta.id}</td>
            <td>{pregunta.nombre}</td>
            <td>{pregunta.question}</td>
            <td>
                <button className="btn btn-primary" onClick={()=>{this.seleccionarEmpresa(pregunta); this.modalInsertar()}}><FontAwesomeIcon icon={faEdit}/></button>
                {"   "}
                <button className="btn btn-danger" onClick={()=>{this.seleccionarEmpresa(pregunta); this.setState({modalEliminar: true})}}><FontAwesomeIcon icon={faTrashAlt}/></button>
                </td>
            </tr>
          )
        })}
        </tbody>
        </table>
        </div>
  );
}
}
export default App;
